Router.configure({
  // the default layout
  layoutTemplate: 'mainLayout'
});    

Router.route('/', function () {

	this.wait(Meteor.subscribe('publicPortfolios'));
	if (this.ready()){
		this.render('home');
		this.layout('mainLayout');
	}else{
// render splash screen... .

	}

});




