	this.wait(Meteor.subscribe('publicPortfolios'));
	if (this.ready()){
		this.render('home');
	}else{
		this.next();
		// render splash screen... .
	}
});

Router.route('/backend/portfolio' , function(){
	this.render('portfolioList');
