Meteor.startup(function() {
  Uploader.finished = function(index, file) {
    Uploads.insert(file);
  }

});


UI.registerHelper('parseToJson', function(context, options) {
  if(context)
  	return JSON.stringify(context);
});


(function(global) {
   	var GROWL = {
	TYPE : {
		SUCCESS : 'success', INFO : 'info',WARN : 'warning' , ERROR : 'error'
	},

	show : function(title,message,type){
		toastr[type](message,title);
	},

	success : function(message){
		GROWL.show('Success',message,growl.TYPE.SUCCESS);
	},

	info : function(message){
		GROWL.show(null,message,growl.TYPE.INFO);
	},

	warn : function(message){
		GROWL.show('Warning',message,growl.TYPE.WARN);
	},

	error : function(message){
		GROWL.show('Error',message,growl.TYPE.ERROR);
	},
};

    global.GROWL = GROWL;
})(this);


