});




Template.portfolioMaintain.onCreated(function () {
	var controller = Iron.controller();
    console.log(controller.params._id);
	//this.subscribe("portfolioById",controller.params._id);
	this.subscribe("portfolioById",'123');
});

Template.portfolioMaintain.helpers({
    item : function () {
    	if(typeof Portfolios.findOne() == 'undefined'){
    		return {
    			title: '',
    			client : '',
    			description : '',
    			splash_image : '',
    			published : ''
    		};

    	}
    	return Portfolios.findOne();
    }
});
