Meteor.startup(function () {

  UploadServer.init({
    tmpDir: process.env.PWD + '/.upload/images/tmp',
    uploadDir: process.env.PWD + '/.upload/images',
    checkCreateDirectories: true, //create the directories for you

	getDirectory: function(fileInfo, formData) {
     // if (formData && formData.directoryName != null) {
     //   return formData.directoryName;
     // }
      return "/images";
    },

    getFileName: function(fileInfo, formData) {
      //if (formData && formData.prefix != null) {
       // return formData.prefix + '_' + fileInfo.name;
      //}
      return formData._id +'_'+fileInfo.name;
    },
    finished: function(fileInfo, formData) {
      if (formData && formData._id != null) {
      		formData.splash_image = fileInfo.path;
      		//console.log(formData);
      		console.log(fileInfo);
        	//Items.update({_id: formData._id}, { $push: { uploads: fileInfo }});
      }
    }
  });
});
