var globalSession = {
  put : function(name,value){
    Session.set(name, value);
  },

  get : function(name){
    Session.get(name);
  },

  sayHello : function(){
    console.log('Hello global session');
  },
}


if (Meteor.isClient) {



  // counter starts at 0
  Session.set('counter', 0);
  console.log('hello');
  Template.page02.helpers({
    counter: function () {
      globalSession.sayHello();
      console.log('hello helper02');
      return Session.get('counter');
    }
  });

  Template.page02.events({
    'click button': function () {
      // increment the counter when button is clicked
      Session.set('counter', Session.get('counter') + 1);
    }
  });
}

if (Meteor.isServer) {
  Meteor.startup(function () {
    // code to run on server at startup
  });
}
